sys_script_include.script:
var BlogUtilAjax = Class.create();
BlogUtilAjax.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	titleExists: function(){
		var title = this.getParameter("sysparam_title");
		var author = this.getParameter("sysparam_author");
		var current = this.getParameter("sysparam_current") || "-1";

		var blog = new GlideRecord("sn_blog_management_blog_post");
		blog.addQuery("title",title);
		blog.addQuery("sys_id","!=",current);
		if("" != author)
			blog.addQuery("author",author);
		blog.query();
		if(blog.next())
			return blog.getValue("sys_id");
		return "-1";
	},

	getTitleDetails: function(){
		var table = this.getParameter("sysparam_table");
		var sys_id = this.getParameter("sysparam_id");
		var column = this.getParameter("sysparam_column");
		var operation = this.getParameter("sysparam_operation");

		if(operation == "read"){
			var gr = new GlideRecord(table);
			gr.get(sys_id);
			return gr.getValue(column);
		} else if (operation == "delete") {
			var gr2 = new GlideRecord(table);
			gr2.get(sys_id);
			gr2.deleteRecord();
		}else if (operation == "insert") {
			var gr3 = new GlideRecord(table);
			var col1 = this.getParameter("sysparam_col1");
			var vl1 = this.getParameter("sysparam_vl1");
			var col2= this.getParameter("sysparam_col2");
			var vl2=this.getParameter("sysparam_vl2");
			gr3.initialize();
			gr3.setValue(col1,vl1);
			gr3.setValue(col2,vl2);
			gr3.insert();

		}

}
    //type: 'BlogUtilAjax'
});
