sys_ui_page.processing_script:
var mail = mailid;
var gr = new GlideRecord('sys_user');
gr.initialize();
gr.addQuery('email', mail);
gr.query();
if(gr.next()){
	var usersysid = gr.sys_id;
}
	var gr1 = new GlideRecord('sys_user_role');
	gr1.initialize();
	gr1.addQuery('sys_name', "sn_blog_management.author");
	gr1.query();
if(gr1.next()){
	var rolesysid = gr1.sys_id;
}
gs.addInfoMessage(rolesysid);
	var gr2 = new GlideRecord('sys_user_has_role');
	gr2.setValue('user', usersysid);
	gr2.setValue('role', rolesysid);
	gr2.update();
